import React, { useState, useEffect } from 'react';
import {
  Container,
  Box,
  Typography,
  Button,
  TextField,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  AppBar,
  Toolbar,
  Alert,
  Paper,
  Divider,
  CircularProgress,
} from '@mui/material';
import {
  Add as AddIcon,
  Delete as DeleteIcon,
  ArrowBack as ArrowBackIcon,
  Book as BookIcon,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { api, Vocabulary, VocabIn } from '../services/api';
import { useAuth } from '../contexts/AuthContext';

const VocabularyList: React.FC = () => {
  const navigate = useNavigate();
  const { isAuthenticated } = useAuth();
  const [vocabularies, setVocabularies] = useState<Vocabulary[]>([]);
  const [open, setOpen] = useState(false);
  const [newWord, setNewWord] = useState('');
  const [newMeaning, setNewMeaning] = useState('');
  const [newExample, setNewExample] = useState('');
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(true);
  const [addingWord, setAddingWord] = useState(false);
  const [deletingId, setDeletingId] = useState<number | null>(null);

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    const fetchVocabulary = async () => {
      try {
        const data = await api.getVocabulary();
        setVocabularies(data);
        setError(null);
      } catch (error) {
        console.error('Error fetching vocabulary:', error);
        setError('Failed to load vocabulary. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchVocabulary();
  }, [isAuthenticated, navigate]);

  const handleAddVocabulary = async () => {
    if (!newWord || !newMeaning) {
      setError('Word and meaning are required');
      return;
    }

    setAddingWord(true);
    setError(null);

    try {
      const newVocabData: VocabIn = {
        word: newWord.trim(),
        meaning: newMeaning.trim(),
      };

      if (newExample.trim()) {
        newVocabData.example = newExample.trim();
      }

      const newVocabulary = await api.addVocabulary(newVocabData);
      setVocabularies(prev => [...prev, newVocabulary]);
      setNewWord('');
      setNewMeaning('');
      setNewExample('');
      setOpen(false);
    } catch (error) {
      console.error('Error adding vocabulary:', error);
      setError('Failed to add new word. Please try again.');
    } finally {
      setAddingWord(false);
    }
  };

  const handleDeleteVocabulary = async (id: number) => {
    setDeletingId(id);
    setError(null);

    try {
      await api.deleteVocabulary(id);
      setVocabularies(prev => prev.filter(vocab => vocab.id !== id));
    } catch (error) {
      console.error('Error deleting vocabulary:', error);
      setError('Failed to delete word. Please try again.');
    } finally {
      setDeletingId(null);
    }
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
      }}
    >
      <AppBar 
        position="static" 
        sx={{ 
          background: 'rgba(255, 255, 255, 0.1)',
          backdropFilter: 'blur(10px)',
          boxShadow: 'none',
        }}
      >
        <Toolbar>
          <IconButton
            edge="start"
            color="inherit"
            onClick={() => navigate('/dashboard')}
            sx={{ mr: 2 }}
          >
            <ArrowBackIcon />
          </IconButton>
          <Box sx={{ flexGrow: 1 }}>
            <Typography 
              variant="h6" 
              component="div"
              sx={{ 
                color: 'white',
                fontWeight: 'bold',
              }}
            >
              Vocabulary List
            </Typography>
            <Typography 
              variant="subtitle2"
              sx={{ 
                color: 'rgba(255, 255, 255, 0.8)',
              }}
            >
              Build and manage your word collection
            </Typography>
          </Box>
          <Button
            color="inherit"
            startIcon={<AddIcon />}
            onClick={() => setOpen(true)}
            sx={{
              background: 'rgba(255, 255, 255, 0.1)',
              backdropFilter: 'blur(10px)',
              borderRadius: '20px',
              padding: '8px 20px',
              color: 'white',
              fontWeight: 'bold',
              border: '1px solid rgba(255, 255, 255, 0.3)',
              '&:hover': {
                background: 'rgba(255, 255, 255, 0.2)',
              },
            }}
          >
            Add New Word
          </Button>
        </Toolbar>
      </AppBar>

      <Container maxWidth="md" sx={{ mt: 4, pb: 4 }}>
        {error && (
          <Alert 
            severity="error" 
            sx={{ 
              mb: 2,
              borderRadius: '10px',
            }}
          >
            {error}
          </Alert>
        )}

        {loading ? (
          <Paper
            sx={{
              p: 4,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              borderRadius: '20px',
              background: 'rgba(255, 255, 255, 0.9)',
              backdropFilter: 'blur(10px)',
              boxShadow: '0 8px 32px 0 rgba(31, 38, 135, 0.37)',
              minHeight: '200px',
            }}
          >
            <CircularProgress size={60} sx={{ color: '#2196F3', mb: 2 }} />
            <Typography variant="h6" sx={{ color: '#7f8c8d' }}>
              Loading your vocabulary...
            </Typography>
          </Paper>
        ) : vocabularies.length === 0 ? (
          <Paper
            sx={{
              p: 4,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              borderRadius: '20px',
              background: 'rgba(255, 255, 255, 0.9)',
              backdropFilter: 'blur(10px)',
              boxShadow: '0 8px 32px 0 rgba(31, 38, 135, 0.37)',
            }}
          >
            <BookIcon sx={{ fontSize: 60, color: '#bdc3c7', mb: 2 }} />
            <Typography variant="h6" sx={{ color: '#7f8c8d', mb: 1 }}>
              Your vocabulary list is empty
            </Typography>
            <Typography variant="body2" sx={{ color: '#95a5a6', textAlign: 'center', mb: 3 }}>
              Start adding words to build your vocabulary collection
            </Typography>
            <Button
              variant="contained"
              startIcon={<AddIcon />}
              onClick={() => setOpen(true)}
              sx={{
                background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
                borderRadius: '25px',
                padding: '10px 24px',
                color: 'white',
                fontWeight: 'bold',
                '&:hover': {
                  background: 'linear-gradient(45deg, #1976D2 30%, #1E88E5 90%)',
                },
              }}
            >
              Add Your First Word
            </Button>
          </Paper>
        ) : (
          <Paper
            sx={{
              borderRadius: '20px',
              background: 'rgba(255, 255, 255, 0.9)',
              backdropFilter: 'blur(10px)',
              overflow: 'hidden',
            }}
          >
            <List sx={{ p: 0 }}>
              {vocabularies.map((vocabulary, index) => (
                <React.Fragment key={vocabulary.id}>
                  <ListItem
                    sx={{
                      p: 3,
                      '&:hover': {
                        background: 'rgba(33, 150, 243, 0.1)',
                      },
                    }}
                  >
                    <ListItemText
                      primary={
                        <Typography variant="h6" sx={{ color: '#2c3e50', fontWeight: 'bold' }}>
                          {vocabulary.word}
                        </Typography>
                      }
                      secondary={
                        <Box sx={{ mt: 1 }}>
                          <Typography variant="body1" sx={{ color: '#34495e' }}>
                            {vocabulary.meaning}
                          </Typography>
                          {vocabulary.example && (
                            <Typography 
                              variant="body2" 
                              sx={{ 
                                color: '#7f8c8d',
                                mt: 0.5,
                                fontStyle: 'italic',
                              }}
                            >
                              Example: {vocabulary.example}
                            </Typography>
                          )}
                        </Box>
                      }
                    />
                    <ListItemSecondaryAction>
                      <IconButton
                        edge="end"
                        aria-label="delete"
                        onClick={() => handleDeleteVocabulary(vocabulary.id)}
                        disabled={deletingId === vocabulary.id}
                        sx={{
                          color: '#e74c3c',
                          '&:hover': {
                            background: 'rgba(231, 76, 60, 0.1)',
                          },
                        }}
                      >
                        {deletingId === vocabulary.id ? 
                          <CircularProgress size={20} color="inherit" /> : 
                          <DeleteIcon />}
                      </IconButton>
                    </ListItemSecondaryAction>
                  </ListItem>
                  {index < vocabularies.length - 1 && (
                    <Divider />
                  )}
                </React.Fragment>
              ))}
            </List>
          </Paper>
        )}
      </Container>

      <Dialog 
        open={open} 
        onClose={() => setOpen(false)}
        PaperProps={{
          sx: {
            borderRadius: '20px',
            boxShadow: '0 8px 32px 0 rgba(31, 38, 135, 0.37)',
          }
        }}
      >
        <DialogTitle sx={{ pb: 1 }}>
          <Typography variant="h6" sx={{ color: '#2c3e50', fontWeight: 'bold' }}>
            Add New Word
          </Typography>
        </DialogTitle>
        <DialogContent>
          <Typography variant="body2" sx={{ color: '#7f8c8d', mb: 2 }}>
            Enter the details of the new word you want to add to your vocabulary list.
          </Typography>
          <TextField
            autoFocus
            margin="dense"
            label="Word"
            fullWidth
            value={newWord}
            onChange={(e) => setNewWord(e.target.value)}
            variant="outlined"
            sx={{ mb: 2 }}
          />
          <TextField
            margin="dense"
            label="Meaning"
            fullWidth
            value={newMeaning}
            onChange={(e) => setNewMeaning(e.target.value)}
            variant="outlined"
            sx={{ mb: 2 }}
          />
          <TextField
            margin="dense"
            label="Example (Optional)"
            fullWidth
            value={newExample}
            onChange={(e) => setNewExample(e.target.value)}
            variant="outlined"
            multiline
            rows={2}
          />
        </DialogContent>
        <DialogActions sx={{ p: 2, pt: 1 }}>
          <Button 
            onClick={() => setOpen(false)}
            sx={{
              color: '#7f8c8d',
              '&:hover': {
                background: 'rgba(127, 140, 141, 0.1)',
              },
            }}
          >
            Cancel
          </Button>
          <Button
            onClick={handleAddVocabulary}
            variant="contained"
            disabled={addingWord}
            sx={{
              background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
              borderRadius: '25px',
              padding: '8px 24px',
              color: 'white',
              fontWeight: 'bold',
              '&:hover': {
                background: 'linear-gradient(45deg, #1976D2 30%, #1E88E5 90%)',
              },
            }}
          >
            {addingWord ? <CircularProgress size={24} color="inherit" /> : 'Add Word'}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default VocabularyList;
